import { HttpClient } from '@angular/common/http';
import { Component, OnInit } from '@angular/core';
import { userService } from '../userService';
import { lastValueFrom } from 'rxjs';


@Component({
  selector: 'app-carta',
  templateUrl: './carta.component.html',
  styleUrls: ['./carta.component.css']
})
export class CartaComponent implements OnInit{

  menus: any[]=[];

  constructor(private http:HttpClient,private userService: userService,){

  }

  async ngOnInit() {
    const req=this.http.get('http://localhost:8080/menus/all');
    let aux:any= await lastValueFrom(req)
    this.menus=aux
  }

}
<link href='https://fonts.googleapis.com/css?family=Chewy' rel='stylesheet'>
<div class="todo">
    <div *ngFor="let menu of this.menus" >
        <h1>{{menu.nombre}}</h1>
        <h2 class="centrado">Primeros</h2>

        <p class="centrado" *ngFor="let primeros of menu.primeros">
            {{primeros}}
        </p>
        <!--ul></ul-->
        <br>
        <h2 class="centrado">Segundos</h2>
        <p class="centrado" *ngFor="let segundos of menu.segundos">
            {{segundos}}
        </p>
        <br>
        <h2 class="centrado">Postres</h2>
            <p class="centrado" *ngFor="let postres of menu.postres">
                {{postres}}
            </p>

        <h2>Precio: {{menu.precio}} euros</h2>
    </div>
</div>
