import { Component } from '@angular/core';
import { userService } from '../userService';
import {FormControl, Validators} from '@angular/forms';
import {MatSnackBar} from '@angular/material/snack-bar';
import { DateAdapter } from '@angular/material/core';

@Component({
  selector: 'app-reservas',
  templateUrl: './reservas.component.html',
  styleUrls: ['./reservas.component.css']
})
export class ReservasComponent {
  selected: Date | null =null;

  email = new FormControl('', [Validators.required, Validators.email]);
  horaSeleccionada?: string;

  horarioControl = new FormControl('');
  horarioComida:string[]=["13:00","13:30","14:00","14:30","15:00"]
  horarioCena:string[]=["20:30","21:00","21:30","22:00","22:30"]
  horarioReal:string[]=this.horarioComida;

  menu1:number=0;
  menu2:number=0;
  menu3:number=0;


  constructor(private userService: userService,private _snackBar: MatSnackBar,public date: DateAdapter<Date>){
    date.getFirstDayOfWeek = () => 1;
  }

  anadirMenu(menu:string, aux:string){
    var comensales:number=parseInt(aux);
    if(comensales>(this.menu1+this.menu2+this.menu3)){
      if(menu=="Menu1"){
        this.menu1++;
      }else if (menu=="Menu2"){
        this.menu2++;
      }else{
        this.menu3++;
      }
    }else{
      this._snackBar.open("No hay tantos comensales", "Cerrar",{duration:3000});
    }
    console.log()
  }
  quitarMenu(menu:string, aux:string){
    var comensales:number=parseInt(aux);
    if(comensales>(this.menu1+this.menu2+this.menu3)){
      if(menu=="Menu1"){
        if(this.menu1>1){
          this.menu1--;
        }else{
          this._snackBar.open("No quedan mas menus 1 en la lista", "Cerrar",{duration:3000});
        }
      }else if (menu=="Menu2"){
        if(this.menu2>1){
          this.menu2--;
        }else{
          this._snackBar.open("No quedan mas menus 2 en la lista", "Cerrar",{duration:3000});
        }
      }else{
        if(this.menu3>1){
          this.menu3--;
        }else{
          this._snackBar.open("No quedan mas menus 3 en la lista", "Cerrar",{duration:3000});
        }
      }
    }else{
      this._snackBar.open("No hay mas menus en la lista", "Cerrar",{duration:3000});
    }

  }

  elegirHorario(){
    var horario=this.horarioControl.value
    if(horario == "Comida"){
      this.horarioReal=this.horarioComida;
    }else{
      this.horarioReal=this.horarioCena;

    }
  }


  reservar(comensales:any){

    if(this.userService.getUser()!=null){
      if(comensales){
        if(this.horaSeleccionada){

          if(this.selected){
            if(comensales=(this.menu1+this.menu2+this.menu3)){

              var date: Date =new Date(this.selected)
              let horas:any=this.horaSeleccionada.split(":");
              date.setHours(parseInt(horas.at(1)),parseInt(horas.at(2)))

              console.log(this.userService.getUser().nombre +" "+comensales)
              console.log(date+ " "+this.selected.getDate())
              console.log(date.getDate() +"/"+(date.getMonth()+1)+"/"+date.getFullYear()+" "+date.getHours()+":"+date.getMinutes())

            }
          }else{
            this._snackBar.open("Introduce el dia de la semana", "Cerrar",{duration:3000});
          }
        }else{
          this._snackBar.open("Introduce la hora de la reserva", "Cerrar",{duration:3000});
        }
      }else{
        this._snackBar.open("Introduce el numero de comensales", "Cerrar",{duration:3000});
      }
    }else{
      this._snackBar.open("Debe estar logueado para reservar", "Cerrar",{duration:3000});
    }
  }

}
