import { Component, OnInit } from '@angular/core';
import { userService } from '../userService';
import { lastValueFrom } from 'rxjs';
import { HttpClient } from '@angular/common/http';
import { Router, UrlSerializer } from '@angular/router';
import { Menu } from '../model/menu';
import { Reserva } from '../model/Reserva';

@Component({
  selector: 'app-reservas-personal',
  templateUrl: './reservas-personal.component.html',
  styleUrls: ['./reservas-personal.component.css']
})
export class ReservasPersonalComponent implements OnInit{

  displayedColumns: string[] = ['fechaReserva', 'comensales', 'menus'];
  dataSource :any;
  menus:string="";

  constructor(private http:HttpClient,private userService: userService,private router: Router){

  }
  async ngOnInit() {
    if(this.userService.getUser()==null){
      this.router.navigate(['/home'])
    }
    if(this.userService.getUser().rol=="admin"){
      const req=this.http.get('http://localhost:8080/reservas/all');
      req.subscribe(aux =>{
        this.dataSource=aux;

      }) 
    }else{
      const req=this.http.post('http://localhost:8080/reservas/getReservasID',""+this.userService.getUser().id);
      req.subscribe(aux =>{
        this.dataSource=aux;

      }) 
    }
    const req=this.http.get('http://localhost:8080/menus/all');
    var salida:any=await lastValueFrom(req)
    var aux:Reserva[]= salida

    var contador =0;

    this.dataSource.forEach((reserva:Reserva) => {

      console.log(salida.at(contador))
      aux.forEach((element:any) => {
        console.log(" test "+ salida+" "+element)
        console.log(element.id +" "+reserva.id_menu.at(contador))
        if(element.id==reserva.id_menu.at(contador)){
          this.menus+=element.nombre+": "+reserva.cantMenu.at(contador)+"    ";
        }
      });
      contador++;
    })

  }
}

<link href='https://fonts.googleapis.com/css?family=Chewy' rel='stylesheet'>
<div class="tabla">
    <h1>Reservas</h1>
    <table mat-table [dataSource]="dataSource" class="mat-elevation-z8">

        <!--- Note that these columns can be defined in any order.
              The actual rendered columns are set as a property on the row definition" -->

        <!-- Position Column -->
        <ng-container matColumnDef="fechaReserva">
          <th mat-header-cell *matHeaderCellDef> Fecha de reserva </th>
          <td mat-cell *matCellDef="let element"> {{element.fechaReserva | date:"dd/MM/YYYY hh:mm"}} </td>
        </ng-container>

        <!-- Name Column -->
        <ng-container matColumnDef="comensales">
          <th mat-header-cell *matHeaderCellDef> Numero de Comensales </th>
          <td mat-cell *matCellDef="let element"> {{element.comensales}} </td>
        </ng-container>

        <!-- Weight Column -->
        <ng-container matColumnDef="menus">
          <th mat-header-cell *matHeaderCellDef> Menus seleccionados </th>
          <td mat-cell *matCellDef="let element"> {{element.menus}} </td>
        </ng-container>

        <tr mat-header-row *matHeaderRowDef="displayedColumns"></tr>
        <tr mat-row *matRowDef="let row; columns: displayedColumns;"></tr>
    </table>
</div>
