import { Component } from '@angular/core';

@Component({
  selector: 'app-navbar',
  templateUrl: './navbar.component.html',
  styleUrls: ['./navbar.component.css']
})
export class NavbarComponent {
  logueado:boolean=false;
  nombre:string="Test";
  ruta:string=""
  constructor(){}

  cambio(){
   this.ruta=this.router.url;
  }
}
