import { HttpClient } from '@angular/common/http';
import { Component, OnInit } from '@angular/core';
import {MatDialog} from '@angular/material/dialog';

@Component({
  selector: 'app-dialogo-menu',
  templateUrl: './dialogo-menu.component.html',
  styleUrls: ['./dialogo-menu.component.css']
})
export class DialogoMenuComponent implements OnInit{

  menus:any[]=[];

  constructor(private matDialog: MatDialog, private http:HttpClient){

  }

  ngOnInit(): void {
    const req=this.http.get('http://localhost:8080/reservas/all');
        req.subscribe((aux:any) =>{
          this.menus=aux;
        }) 
  }
  closeDialog(){
    this.matDialog.closeAll()
  }
}
