<div class="todo">
  <div>
    <h1>Contacto</h1>
    <div class="info">


      <div class="element">
        <mat-icon class="iconos" fontIcon="location_on"></mat-icon> 
        <a class="text">Aragon 74, Montalban, Teruel</a>
      </div>
      <br>
      <div class="element">
        <mat-icon class="iconos" fontIcon="phone"></mat-icon> 
        <a class="text">+34 622 64 66 30</a>
      </div>
      <br>
      <div class="element">
        <mat-icon class="iconos" fontIcon="email"></mat-icon> 
        <a class="text">aa21.amallen98@iespabloserrano.com</a>
      </div>
    </div>
  </div>


  <div class="recuadro">

    <h1>Reserva</h1>
    <div class="elementos">

      <div class="izquierda">
        <div>
        <div class="comensales">
          <mat-icon class="iconos" fontIcon="people"></mat-icon> 
          <input type="text" placeholder="Introduce Nº comensales" required #comensales>
        </div>
        <img class="linea" src="../../assets/linea2.png"  alt="Entrada"/>
        <div class="menus">
          <div class="menu1">
            <mat-icon (click)="anadirMenu('Menu1')" fontIcon="add_circle_outline"></mat-icon>
            <mat-icon (click)="quitarMenu('Menu1')" fontIcon="remove_circle_outline"></mat-icon>
            <a>Menu 1</a>
          </div>

          menus
        </div>
      </div>
      </div>


      <div class="derecha"> 
        <mat-card class="demo-inline-calendar-card">
          <mat-calendar [(selected)]="selected"></mat-calendar>
        </mat-card>
        <p>Selected date: {{selected}}</p>
      </div>

    </div>

  </div>


</div>
